package wk1.d1;

public class IsPrime {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 10;
		for (int i = 2; i<= n-1; i++) {
			if (n/2== 0) {
				System.out.println("The given number is prime number" +n);
				
			}else
			{ System.out.println("The given number is not a prime number"+n);
		}
	}

}
}